import axios from 'axios';
import React from 'react'

//import { useState } from 'react';
function Add() {
    const handleSubmit = async (event) => {
      event.preventDefault();
      const formData = new FormData(event.target);
      const response = await axios.post('http://127.0.0.1:5000/api/items/add', formData);
      console.log(response.data);
  }
  return (
    <>
    <form action='http://127.0.0.1:5000/api/items/add' method='post' onSubmit={handleSubmit}>
        <div className="d-flex align-items-center flex-column">
            <div className="justify-content-center my-5">
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" name="title"    placeholder="title"    />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" name="text"     placeholder="text"     />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" name="prix"     placeholder="prix"     />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" name="category" placeholder="category" />
                <button className="btn btn-info mt-2">send</button> 
            </div>


        </div>
    </form>
    </>
  )
}

export default Add
