import { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { Nav , Home , Footer , Men , Women} from "./components/index";
function App() {
  const [items, setItems] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [item,setitem] = useState([])
  function getitem(){
    axios.get("http://127.0.0.1:5000/api/data")
    .then(res => {setitem(res.data)})
  }

  function postitem(){
    axios.post("http://127.0.0.1:5000/api/items")
    .then(res => {setItems(res.data)})
  }
  useEffect(()=>{
    getitem()
    postitem()
  },[])
  return (
    <>
    <BrowserRouter>
      <Nav/>
      <Routes>
       <Route path="/"      element={<Home  item={items}/>} />
       <Route path="/Men"   element={<Men   item={item}/>} />
       <Route path="/Women" element={<Women item={item}/>} />
      </Routes>
       <Footer/>
    </BrowserRouter>
    </>
  );
}

export default App;
