import React from 'react'
import { useState } from 'react';
function Add() {
  const [inputValue, setInputValue] = useState('');
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };
  const handleSubmit = (event) => {
    event.preventDefault();}
  return (
    <>

        <form action='http://127.0.0.1:5000/api/items/add' method='post' onSubmit={handleSubmit}>
        <div className="container w-100 mt-5 py-5 justify-content-center">
            <div class="input-group">
                <span class="input-group-text">title and last prix</span>
                <input type="text" name='title' value={inputValue} onChange={setInputValue()} class="form-control"/>
                <input type="text" name='prix'  class="form-control"/>
                {/* <input type="text" name='category'  class="form-control"/> */}
            </div>
            <div class="mb-3 mt-5">
                <label for="exampleFormControlTextarea1" class="form-label">Description</label>
                <input class="form-control" id="exampleFormControlTextarea1" name='text' rows="3"/>
            </div>
            <button className='btn btn-info text-center' type="submit">create products</button>
        </div>

        </form>
    </>
  )
}

export default Add
