import { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { Nav , Home , Footer , Men , Women , Add } from "./components/index";
function App() {
  const [items, setItems] = useState([]);
  const [title, settitle] = useState('');
  const [text, settext] = useState('');
  const [prix, setprix] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/api/items')
      .then(response => setItems(response.data))
      .catch(error => console.log(error));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('http://127.0.0.1:5000/api/items', { title, text, prix })
        .then(response => {
        setItems([...items, response.data]);
        settitle('');
        settext('');
        setprix('');
      })
      .catch(error => console.log(error));
  };
  return (
    <>
    <BrowserRouter>
      <Nav/>
      <Routes>
       <Route path="/"      element={<Home  item={items} />} />
       <Route path="/Men"   element={<Men   item={items} />} />
       <Route path="/Women" element={<Women item={items} />} />
       <Route path="/add"   element={<Add func={handleSubmit} ti={settext}te={settext} pr={setprix}/>}/>
      </Routes>
       <Footer/>
    </BrowserRouter>
    </>
  );
}

export default App;
