import axios from 'axios';
import React from 'react'

import { useState } from 'react';
function Add() {
  const [clear , setClear] = useState('')

    const handleSubmit = async (event) => {
      event.preventDefault();
      const formData = new FormData(event.target);
      const response = await axios.post('http://127.0.0.1:5000/api/items/add', formData);
      console.log(response.data)
      alert("nice brow")
      setClear('')
    }
    const show = (event) =>{
      setClear(event.target.value)
    }
  return (
    <>
    <form onSubmit={handleSubmit}>
        <div className="d-flex align-items-center flex-column">
            <div className="justify-content-center my-5">
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" value={title}    onChange={show} name="title"    placeholder="title"    />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" value={text}     onChange={show} name="text"     placeholder="text"     />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" value={prix}     onChange={show} name="prix"     placeholder="prix"     />
                <input type="text" style={{width : "20rem"}} className="form-control p-2 mt-3" value={category} onChange={show} name="category" placeholder="category" />
                <button className="btn btn-info mt-2">send</button> 
            </div>


        </div>
    </form>
    </>
  )
}

export default Add
